import React, { useState } from 'react';
import './ReservationForm.css';

function ReservationForm() {
  const [reservationData, setReservationData] = useState({
    date: '2024-06-30', // Beispiel für ein vordefiniertes Datum
    time: '18:00', // Beispiel für eine vordefinierte Uhrzeit
    customer_name: 'Max Mustermann', // Beispiel für einen vordefinierten Kundenname
    guest_count: '4', // Beispiel für eine vordefinierte Anzahl an Gästen
    employee_name: 'Mitarbeiter', // Beispiel für einen vordefinierten Mitarbeiternamen
    table_number: '12', // Beispiel für eine vordefinierte Tischnummer
    phone_number: '0123456789' // Beispiel für eine vordefinierte Telefonnummer
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Token aus dem lokalen Speicher abrufen
      const token = localStorage.getItem('token');
      console.log("[ReservationForm] Token:", token); // Token loggen

      const response = await fetch('http://localhost:5000/api/reservations', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'x-auth-token': token // Token aus dem lokalen Speicher einfügen
        },
        body: JSON.stringify(reservationData)
      });
      const data = await response.json();
      console.log(data);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleChange = (e) => {
    setReservationData({ ...reservationData, [e.target.name]: e.target.value });
  };

  return (
    <div className="reservation-form">
      <h2>Reservierung erstellen</h2>
      <form onSubmit={handleSubmit}>
        <label htmlFor="date">Datum:</label>
        <input
          type="date"
          id="date"
          name="date"
          value={reservationData.date}
          onChange={handleChange}
        />
        <label htmlFor="time">Uhrzeit:</label>
        <input
          type="time"
          id="time"
          name="time"
          value={reservationData.time}
          onChange={handleChange}
        />
        <label htmlFor="customer_name">Kundenname:</label>
        <input
          type="text"
          id="customer_name"
          name="customer_name"
          value={reservationData.customer_name}
          onChange={handleChange}
        />
        <label htmlFor="guest_count">Anzahl der Gäste:</label>
        <input
          type="number"
          id="guest_count"
          name="guest_count"
          value={reservationData.guest_count}
          onChange={handleChange}
        />
        <label htmlFor="employee_name">Mitarbeitername:</label>
        <input
          type="text"
          id="employee_name"
          name="employee_name"
          value={reservationData.employee_name}
          onChange={handleChange}
        />
        <label htmlFor="table_number">Tischnummer:</label>
        <input
          type="text"
          id="table_number"
          name="table_number"
          value={reservationData.table_number}
          onChange={handleChange}
        />
        <label htmlFor="phone_number">Telefonnummer:</label>
        <input
          type="tel"
          id="phone_number"
          name="phone_number"
          value={reservationData.phone_number}
          onChange={handleChange}
        />
        <input type="submit" value="Reservieren" />
      </form>
    </div>
  );
}

export default ReservationForm;
