import React, { useState, useEffect } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import ReservationForm from './components/ReservationForm';
import ReservationList from './components/ReservationList';
import { Container, Row, Col } from 'react-bootstrap';
import './App.css';

function App() {
  const [date, setDate] = useState(new Date());
  const [reservations, setReservations] = useState([]);

  const formattedDate = date.toISOString().split('T')[0]; // Formatieren des Datums f端r API-Anfrage

  const fetchReservations = async (selectedDate) => {
    try {
      const response = await fetch(`http://localhost:5000/api/reservations?date=${selectedDate}`);
      const data = await response.json();
      // Reservierungen nach Uhrzeit sortieren (Beispiel: sortieren nach time)
      const sortedReservations = data.sort((a, b) => a.time.localeCompare(b.time));
      setReservations(sortedReservations);
    } catch (error) {
      console.error('Error fetching reservations:', error);
    }
  };

  useEffect(() => {
    fetchReservations(formattedDate); // Datumsformat 端bergeben
  }, [date, formattedDate]);

  return (
    <Container fluid className="App">
      <h1 className="text-center mt-4">Reservierungssystem</h1>
      <Row className="justify-content-center mt-4">
        <Col md={6} className="calendar-container">
          <Calendar
            onChange={setDate}
            value={date}
          />
        </Col>
      </Row>
      <Row className="justify-content-center mt-4">
        <Col md={6}>
          <ReservationList selectedDate={formattedDate} /> {/* Hier formattedDate verwenden */}
        </Col>
      </Row>
      <Row className="justify-content-center mt-4">
        <Col md={6}>
          <ReservationForm selectedDate={formattedDate} /> {/* Hier formattedDate verwenden */}
        </Col>
      </Row>
    </Container>
  );
}

export default App;
