import React, { useState } from 'react';
import './ReservationForm.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

function ReservationForm({ selectedDate }) {
  const formatDate = (date) => {
    const d = new Date(date);
    const formattedDate = `${d.getDate()}/${d.getMonth() + 1}/${d.getFullYear()}`;
    return formattedDate;
  };

  const [reservationData, setReservationData] = useState({
    date: formatDate(selectedDate), // Datum im Format 'DD/MM/YYYY'
    time: '12:00', // Beispielzeit
    customer_name: 'Max Mustermann', // Beispielname
    guest_count: '4', // Beispielanzahl GÃ¤ste
    employee_name: 'Anna Mitarbeiter', // Beispielname des Mitarbeiters
    table_number: '5', // Beispiel Tischnummer
    phone_number: '123456789' // Beispiel Telefonnummer
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/reservations', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'x-auth-token': token
        },
        body: JSON.stringify(reservationData)
      });
      const data = await response.json();
      console.log(data);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleChange = (e) => {
    setReservationData({ ...reservationData, [e.target.name]: e.target.value });
  };

  return (
    <div>
    <div className="reservation-form">
      <h2>Reservierung erstellen fÃ¼r {formatDate(selectedDate)}</h2>
      <form onSubmit={handleSubmit}>
        <label htmlFor="time">Uhrzeit:</label>
        <input
          type="time"
          id="time"
          name="time"
          value={reservationData.time}
          onChange={handleChange}
        />
        <label htmlFor="customer_name">Kundenname:</label>
        <input
          type="text"
          id="customer_name"
          name="customer_name"
          value={reservationData.customer_name}
          onChange={handleChange}
        />
        <label htmlFor="guest_count">Anzahl der GÃ¤ste:</label>
        <input
          type="number"
          id="guest_count"
          name="guest_count"
          value={reservationData.guest_count}
          onChange={handleChange}
        />
        <label htmlFor="employee_name">Mitarbeitername:</label>
        <input
          type="text"
          id="employee_name"
          name="employee_name"
          value={reservationData.employee_name}
          onChange={handleChange}
        />
        <label htmlFor="table_number">Tischnummer:</label>
        <input
          type="text"
          id="table_number"
          name="table_number"
          value={reservationData.table_number}
          onChange={handleChange}
        />
        <label htmlFor="phone_number">Telefonnummer:</label>
        <input
          type="tel"
          id="phone_number"
          name="phone_number"
          value={reservationData.phone_number}
          onChange={handleChange}
        />
        <input type="submit" value="Reservieren" />
      </form>
    </div>

    <div>
      <Form onSubmit={handleSubmit}>

      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Uhrzeit</Form.Label>
        <Form.Control type="time" id="time" name="time" />
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label>Kundenname</Form.Label>
        <Form.Control type="text"
          id="customer_name"
          name="customer_name"
          value={reservationData.customer_name}
          onChange={handleChange} />
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label>Anzahl der GÃ¤ste</Form.Label>
        <Form.Control type="number"
          id="guest_count"
          name="guest_count"
          value={reservationData.guest_count}
          onChange={handleChange} />
      </Form.Group>


      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label>Mitarbeitername</Form.Label>
        <Form.Control type="text"
          id="employee_name"
          name="employee_name"
          value={reservationData.employee_name}
          onChange={handleChange} />
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label>Tischnummer</Form.Label>
        <Form.Control type="text"
          id="table_number"
          name="table_number"
          value={reservationData.table_number}
          onChange={handleChange} />
      </Form.Group>


      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label>Telefonnummer</Form.Label>
        <Form.Control type="tel"
          id="phone_number"
          name="phone_number"
          value={reservationData.phone_number}
          onChange={handleChange} />
      </Form.Group> 

      <Button variant="primary" type="submit">
        Speichern
      </Button>
    </Form>
    </div>

    </div>
  );
}

export default ReservationForm;
