import React, { useState } from 'react';
import axios from 'axios';

const ReservationForm = () => {
  // Zustände für die Formulareingaben
  const [formData, setFormData] = useState({
    date: '',
    time: '',
    customerName: '',
    numGuests: '',
    tableName: '',
    phoneNumber: ''
  });

  // Handler-Funktion zum Aktualisieren der Formulareingaben
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Handler-Funktion zum Einreichen des Formulars
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // HTTP-POST-Anfrage an das Backend senden
      const response = await axios.post('http://localhost:5000/api/reservations', formData);
      console.log('Reservation created:', response.data);
      // Formulardaten zurücksetzen
      setFormData({
        date: '',
        time: '',
        customerName: '',
        numGuests: '',
        tableName: '',
        phoneNumber: ''
      });
    } catch (error) {
      console.error('Error creating reservation:', error.message);
    }
  };

  return (
    <div>
      <h2>Reservierungsformular</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Datum:</label>
          <input type="date" name="date" value={formData.date} onChange={handleChange} />
        </div>
        <div>
          <label>Uhrzeit:</label>
          <input type="time" name="time" value={formData.time} onChange={handleChange} />
        </div>
        <div>
          <label>Name des Kunden:</label>
          <input type="text" name="customerName" value={formData.customerName} onChange={handleChange} />
        </div>
        <div>
          <label>Anzahl der Gäste:</label>
          <input type="number" name="numGuests" value={formData.numGuests} onChange={handleChange} />
        </div>
        <div>
          <label>Tischnummer:</label>
          <input type="text" name="tableName" value={formData.tableName} onChange={handleChange} />
        </div>
        <div>
          <label>Telefonnummer:</label>
          <input type="text" name="phoneNumber" value={formData.phoneNumber} onChange={handleChange} />
        </div>
        <button type="submit">Reservieren</button>
      </form>
    </div>
  );
};

export default ReservationForm;
