// App.js

import React, { useState, useEffect } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import ReservationForm from './components/ReservationForm';
import './App.css';

function App() {
  const [date, setDate] = useState(new Date());
  const [reservations, setReservations] = useState([]);

  const fetchReservations = async (selectedDate) => {
    try {
      const formattedDate = selectedDate.toISOString().split('T')[0];
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000/api/reservations?date=${formattedDate}`, {
        headers: {
          'x-auth-token': token,
        },
      });
      const data = await response.json();
      setReservations(data);
    } catch (error) {
      console.error('Error fetching reservations:', error);
    }
  };

  useEffect(() => {
    fetchReservations(date);
  }, [date]);

  return (
    <div className="App">
      <h1>Reservierungssystem</h1>
      <div className="calendar-container">
        <Calendar
          onChange={setDate}
          value={date}
        />
      </div>
      <div className="reservations-list">
        <h2>Reservierungen für {date.toISOString().split('T')[0]}</h2>
        {reservations.length > 0 ? (
          <ul>
            {reservations.map((reservation) => (
              <li key={reservation.id}>
                {reservation.time} - {reservation.customer_name} - {reservation.guest_count} Gäste
              </li>
            ))}
          </ul>
        ) : (
          <p>Keine Reservierungen für dieses Datum.</p>
        )}
      </div>
      <ReservationForm selectedDate={date} />
    </div>
  );
}

export default App;
