// ReservationForm.js

import React, { useState } from 'react';
import { Form, Button } from 'react-bootstrap';
import './ReservationForm.css';

function ReservationForm({ selectedDate }) {
  const [reservationData, setReservationData] = useState({
    date: selectedDate, // Hier wird das Datum als formatierter String erwartet, nicht als Date-Objekt
    time: '12:00', // Beispielzeit
    customer_name: 'Max Mustermann', // Beispielname
    guest_count: '4', // Beispielanzahl Gäste
    employee_name: 'Anna Mitarbeiter', // Beispielname des Mitarbeiters
    table_number: '5', // Beispiel Tischnummer
    phone_number: '123456789' // Beispiel Telefonnummer
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/reservations', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'x-auth-token': token
        },
        body: JSON.stringify(reservationData)
      });
      const data = await response.json();
      console.log(data);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleChange = (e) => {
    setReservationData({ ...reservationData, [e.target.name]: e.target.value });
  };

  return (
    <div className="reservation-form">
      <h2 className="text-center">Reservierung erstellen für {selectedDate}</h2>
      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="time">
          <Form.Label>Uhrzeit:</Form.Label>
          <Form.Control
            type="time"
            name="time"
            value={reservationData.time}
            onChange={handleChange}
          />
        </Form.Group>
        <Form.Group controlId="customer_name">
          <Form.Label>Kundenname:</Form.Label>
          <Form.Control
            type="text"
            name="customer_name"
            value={reservationData.customer_name}
            onChange={handleChange}
          />
        </Form.Group>
        <Form.Group controlId="guest_count">
          <Form.Label>Anzahl der Gäste:</Form.Label>
          <Form.Control
            type="number"
            name="guest_count"
            value={reservationData.guest_count}
            onChange={handleChange}
          />
        </Form.Group>
        <Form.Group controlId="employee_name">
          <Form.Label>Mitarbeitername:</Form.Label>
          <Form.Control
            type="text"
            name="employee_name"
            value={reservationData.employee_name}
            onChange={handleChange}
          />
        </Form.Group>
        <Form.Group controlId="table_number">
          <Form.Label>Tischnummer:</Form.Label>
          <Form.Control
            type="text"
            name="table_number"
            value={reservationData.table_number}
            onChange={handleChange}
          />
        </Form.Group>
        <Form.Group controlId="phone_number">
          <Form.Label>Telefonnummer:</Form.Label>
          <Form.Control
            type="tel"
            name="phone_number"
            value={reservationData.phone_number}
            onChange={handleChange}
          />
        </Form.Group>
        <Button variant="primary" type="submit">
          Reservieren
        </Button>
      </Form>
    </div>
  );
}

export default ReservationForm;
