// App.js

import React, { useState, useEffect } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import ReservationForm from './components/ReservationForm';
import ReservationList from './components/ReservationList';
import { Container, Row, Col } from 'react-bootstrap';
import './App.css';

function App() {
  const [date, setDate] = useState(new Date());
  const [reservations, setReservations] = useState([]);

  const fetchReservations = async (selectedDate) => {
    try {
      const formattedDate = selectedDate.toISOString().split('T')[0];
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000/api/reservations?date=${formattedDate}`, {
        headers: {
          'x-auth-token': token,
        },
      });
      const data = await response.json();
      setReservations(data);
    } catch (error) {
      console.error('Error fetching reservations:', error);
    }
  };

  useEffect(() => {
    fetchReservations(date);
  }, [date]);

  return (
    <Container fluid className="App">
      <h1 className="text-center mt-4">Reservierungssystem</h1>
      <Row className="justify-content-center mt-4">
        <Col md={6} className="calendar-container">
          <Calendar
            onChange={setDate}
            value={date}
          />
        </Col>
      </Row>
      <Row className="justify-content-center mt-4">
        <Col md={6}>
          <ReservationList selectedDate={date} />
        </Col>
      </Row>
      <Row className="justify-content-center mt-4">
        <Col md={6}>
          <ReservationForm selectedDate={date} />
        </Col>
      </Row>
    </Container>
  );
}

export default App;
